INSERT INTO `ey_article_content` VALUES ('33', '38', '&lt;p&gt;去贵公司参观过，对贵公司有一定了解，一个企业如果没有长远的战略眼光，也许在三年五年之内企业做的如日中天，却在十年之后反而被当初不屑一顾的弱小对手轻易地吃掉。公司能发展到今天，说明贵公司在思考整个行业未来发展趋势并合理调整战略规划，率先做出布局，和这样有战略眼光的公司合作，值得信赖。&lt;/p&gt;', '1534559427', '1534559427');
INSERT INTO `ey_article_content` VALUES ('34', '39', '&lt;p&gt;产品质量是企业发展的基础，是企业的生命，也是我们共同的追求。和贵公司合作期间，他们以优良的产品质量和完善的服务来满足我们的需求。贵公司注重品牌，品质，诚信和服务，赢得广大客户高度认同。&lt;/p&gt;', '1534559502', '1534559502');
INSERT INTO `ey_article_content` VALUES ('35', '40', '&lt;p&gt;一次偶然的机会，在广交会展上面认识了贵公司，被他们丰富的产品吸引。刚好我们公司需要酒店通风的情况，就抱着试一试的心态先试了小样，然后订购了一批；之后做出来的成品发现质量非常棒，贵公司售后服务这块也非常到位，总而言之，非常满意。&lt;/p&gt;', '1534559541', '1534559541');
INSERT INTO `ey_article_content` VALUES ('36', '41', '&lt;p&gt;一直继承和发扬了“诚信为本，以信取利”的商业精髓，为顾客提供完善的售前、售中和售后服务，赢得了广泛的市场美誉和良好的口碑，也因此在这一行里脱颖而出。他们凭借着自己的热情、责任和专注，以及优异的产品质量和差异化的特色，完完全全的折服了我们。加油，我们愿意和你一起再次腾飞！&lt;/p&gt;', '1534559566', '1534559566');
INSERT INTO `ey_article_content` VALUES ('37', '42', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/08/18/a604e738c1a643fe5828b6703d019a82.jpg&quot; title=&quot;a604e738c1a643fe5828b6703d019a82.jpg&quot; alt=&quot;a604e738c1a643fe5828b6703d019a82.jpg&quot;/&gt;&lt;/p&gt;', '1534559667', '1534559667');
INSERT INTO `ey_article_content` VALUES ('38', '43', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/08/18/ef3c15d2e77fad52015809b4cad5d226.jpg&quot; title=&quot;ef3c15d2e77fad52015809b4cad5d226.jpg&quot; alt=&quot;ef3c15d2e77fad52015809b4cad5d226.jpg&quot;/&gt;&lt;/p&gt;', '1534559692', '1534559692');
INSERT INTO `ey_article_content` VALUES ('39', '44', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/08/18/6e4c36b70dcf2f4c59fe8cd508b3c6db.jpg&quot; title=&quot;6e4c36b70dcf2f4c59fe8cd508b3c6db.jpg&quot; alt=&quot;6e4c36b70dcf2f4c59fe8cd508b3c6db.jpg&quot;/&gt;&lt;/p&gt;', '1534559708', '1534559708');
INSERT INTO `ey_article_content` VALUES ('40', '45', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/08/18/54ada410638d3720da2ef0d966052eec.jpg&quot; title=&quot;54ada410638d3720da2ef0d966052eec.jpg&quot; alt=&quot;54ada410638d3720da2ef0d966052eec.jpg&quot;/&gt;&lt;/p&gt;', '1534559726', '1534559726');
INSERT INTO `ey_article_content` VALUES ('41', '46', '&lt;p&gt;去贵公司参观过，对贵公司有一定了解，一个企业如果没有长远的战略眼光，也许在三年五年之内企业做的如日中天，却在十年之后反而被当初不屑一顾的弱小对手轻易地吃掉。公司能发展到今天，说明贵公司在思考整个行业未来发展趋势并合理调整战略规划，率先做出布局，和这样有战略眼光的公司合作，值得信赖。&lt;/p&gt;', '1534560439', '1534560439');
INSERT INTO `ey_article_content` VALUES ('42', '47', '&lt;p&gt;产品质量是企业发展的基础，是企业的生命，也是我们共同的追求。和贵公司合作期间，他们以优良的产品质量和完善的服务来满足我们的需求。贵公司注重品牌，品质，诚信和服务，赢得广大客户高度认同。&lt;/p&gt;', '1534560467', '1534560467');
INSERT INTO `ey_article_content` VALUES ('43', '48', '&lt;p&gt;一次偶然的机会，在广交会展上面认识了贵公司，被他们丰富的产品吸引。刚好我们公司需要酒店通风的情况，就抱着试一试的心态先试了小样，然后订购了一批；之后做出来的成品发现质量非常棒，贵公司售后服务这块也非常到位，总而言之，非常满意。&lt;/p&gt;', '1534560488', '1534560488');
INSERT INTO `ey_article_content` VALUES ('44', '58', '&lt;p style=&quot;text-align:center&quot;&gt;&lt;img src=&quot;/public/upload/article/2018/08/18/713b1014e2a9584cd4b0692838ae1141.jpg&quot; title=&quot;713b1014e2a9584cd4b0692838ae1141.jpg&quot; alt=&quot;713b1014e2a9584cd4b0692838ae1141.jpg&quot;/&gt;&lt;/p&gt;', '1534573880', '1534573880');
